function addProduct(n){$.ajax({type:"POST",url:"WebShop/AddProductToCart",data:{productID:n},success:function(t){t.productCount===1&&$("#"+n+"is-in-cart").show();var i=$("#number-of-items-nav").text();i++;$("#number-of-items-nav").text(i);$("#number-of-items").text(i);$("#shop-nav-bar-item").css("color","red")}})}$(function(){var n=$("#action-holder").data("request-url");$.ajax({type:"GET",url:n,success:function(n){console.log(n);$("#number-of-items").text(n);n>0&&$("#shop-nav-bar-item").css("color","red")}})});