function addProduct(n,t){$.ajax({type:"POST",url:"AddProductToCart",data:{productID:n},success:function(n){$("#"+t+"quantity").text(n.productCount);$("#total-price").text(n.totalPrice.toFixed(2)+" kn");$("#"+t+"cart-item-price").text("Ukupno: "+n.cartItemPrice.toFixed(2)+" kn");$("#number-of-items-nav").text(n.numberOfCartItems);n.numberOfCartItems>0&&$("#shop-nav-bar-item").css("color","red")}})}function removeProduct(n){$.ajax({type:"POST",url:"RemoveProductFromCart",data:{cartItemID:n},success:function(t){t.productCount===0&&$("#"+n).remove();var i=$(".cart-item").length;i<=0&&($("#cart-details").hide(),$("#cart-empty").show());$("#"+n+"quantity").text(t.productCount);$("#total-price").text(t.totalPrice.toFixed(2)+" kn");$("#"+n+"cart-item-price").text("Ukupno "+t.cartItemPrice.toFixed(2)+" kn");$("#number-of-items-nav").text(t.numberOfCartItems);t.numberOfCartItems===0&&$("#shop-nav-bar-item").css("color","#9d9d9d")}})}function deleteProduct(n){$.ajax({type:"POST",url:"DeleteProductFromCart",data:{cartItemID:n},success:function(t){$("#"+n).remove();$("#total-price").text(t.totalPrice.toFixed(2)+" kn");var i=$(".cart-item").length;i<=0&&($("#cart-details").hide(),$("#cart-empty").show());$("#number-of-items-nav").text(t.numberOfCartItems);t.numberOfCartItems===0&&$("#shop-nav-bar-item").css("color","#9d9d9d")}})}